import React, { useState } from "react";
import axios from "axios";
import { BarChart, Bar, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from "recharts";

function Home() {
  // const [food, setFood] = useState("");
  // const [qty, setQty] = useState(1);
  // const [items, setItems] = useState([]);
  const [bmiData, setBmiData] = useState({ weight: "", height: "", bmi: null, category: "" });

  // const addItem = async () => {
  //   try {
  //     const res = await axios.get("http://localhost:5000/nutrients", { food, quantity: qty });
  //     setItems([...items, res.data]);
  //     setFood("");
  //     setQty(1);
  //   } catch (e) {
  //     alert(e.response?.data?.error || "Error");
  //   }
  // };

  const calcBMI = async () => {
    try {
      const res = await axios.post("http://localhost:5000/bmi", {
        weight: bmiData.weight,
        height: bmiData.height,
      });
      setBmiData({ ...bmiData, bmi: res.data.bmi, category: res.data.category });
    } catch {
      alert("Error calculating BMI");
    }
  };

  return (
    <div style={{ maxWidth: "800px", margin: "auto", padding: 20 }}>
      <h1>Health & Fitness Dashboard</h1>

      {/* Food Entry
      <section>
        <h2>Add Food</h2>
        <input placeholder="Food name" value={food} onChange={e => setFood(e.target.value)} />
        <input type="number" min="0.1" step="0.1" value={qty} onChange={e => setQty(e.target.value)} /> qty
        <button onClick={addItem}>Add</button>
      </section> */}

      {/* Bar Chart
      {items.length > 0 && (
        <section style={{ height: 300, marginTop: 40 }}>
          <h2>Nutrient Breakdown</h2>
          <ResponsiveContainer width="100%" height="100%">
            <BarChart data={items} margin={{ left: 50 }}>
              <XAxis dataKey="food" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="calories" fill="#8884d8" />
              <Bar dataKey="protein" fill="#82ca9d" />
              <Bar dataKey="carbs" fill="#ffc658" />
              <Bar dataKey="fat" fill="#ff8042" />
            </BarChart>
          </ResponsiveContainer>
        </section>
      )} */}

      {/* Items Table
      {items.length > 0 && (
        <table border="1" cellPadding="8" style={{ marginTop: 20, width: "100%" }}>
          <thead>
            <tr><th>Food</th><th>Qty</th><th>Cal</th><th>Protein</th><th>Carbs</th><th>Fat</th><th>Fiber</th><th>Vit C</th></tr>
          </thead>
          <tbody>
            {items.map((it, i) => (
              <tr key={i}>
                <td>{it.food}</td><td>{it.quantity}</td><td>{it.calories}</td>
                <td>{it.protein}</td><td>{it.carbs}</td><td>{it.fat}</td>
                <td>{it.fiber}</td><td>{it.vitaminC}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )} */}

      {/* BMI Calculator */}
      <section style={{ marginTop: 40 }}>
        <h2>BMI Calculator</h2>
        <input placeholder="Weight (kg)" value={bmiData.weight} onChange={e => setBmiData({ ...bmiData, weight: e.target.value })} />
        <input placeholder="Height (cm)" value={bmiData.height} onChange={e => setBmiData({ ...bmiData, height: e.target.value })} />
        <button onClick={calcBMI}>Calculate</button>
        {bmiData.bmi && (
          <p>Your BMI: <strong>{bmiData.bmi}</strong> ({bmiData.category})</p>
        )}
      </section>
    </div>
  );
}

export default Home;
