import React from "react";
import { FitnessCard } from "./components/FitnessCard";
import { NutritionCard } from "./components/NutritionCard";

function Home() {
  return (
    <div className="font-sans text-gray-800">
      <nav className="bg-green-600 text-white p-4 flex justify-between items-center shadow-md">
        <h1 className="text-2xl font-bold">FitLife Guide</h1>
        <div className="space-x-6">
          <a href="#fitness" className="hover:text-yellow-200">Fitness</a>
          <a href="#nutrition" className="hover:text-yellow-200">Nutrition</a>
        </div>
      </nav>

      <header className="bg-cover bg-center h-[60vh] flex items-center justify-center text-white" style={{ backgroundImage: "url('https://images.unsplash.com/photo-1605296867304-46d5465a13f1')" }}>
        <div className="bg-black bg-opacity-50 p-6 rounded">
          <h2 className="text-4xl font-bold mb-4">Your Journey to a Healthier You</h2>
          <p className="text-lg">Get expert fitness and nutritional guidance tailored to your goals.</p>
        </div>
      </header>

      <section id="fitness" className="p-8 bg-gray-100">
        <h3 className="text-3xl font-bold mb-6 text-center">Fitness Tips</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <FitnessCard title="Full Body Workouts" image="https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b" description="Build strength and endurance with balanced training routines." />
          <FitnessCard title="Yoga & Flexibility" image="https://images.unsplash.com/photo-1554306274-f2070f45c6e4" description="Improve posture, breathing, and flexibility through yoga." />
          <FitnessCard title="HIIT & Cardio" image="https://images.unsplash.com/photo-1583454110550-5be3c78c63b2" description="Burn fat fast with high-intensity interval training." />
        </div>
      </section>

      <section id="nutrition" className="p-8 bg-white">
        <h3 className="text-3xl font-bold mb-6 text-center">Nutritional Guidance</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <NutritionCard title="Balanced Diet Plans" image="https://images.unsplash.com/photo-1512058564366-c9e7b4a2a112" description="Customized meal plans based on your body type and goals." />
          <NutritionCard title="Superfoods" image="https://images.unsplash.com/photo-1584270354949-1b213a5aaf03" description="Discover foods that boost immunity, energy, and metabolism." />
          <NutritionCard title="Hydration & Supplements" image="https://images.unsplash.com/photo-1605296867301-0c5ed3c87f4a" description="Learn how to hydrate effectively and choose the right supplements." />
        </div>
      </section>

      <footer className="bg-gray-800 text-white text-center py-4">
        Â© 2025 FitLife Guide. All rights reserved.
      </footer>
    </div>
  );
}

export default Home;
