import React, { useState } from 'react';
import axios from 'axios';

function MainApp() {
  const [userData, setUserData] = useState({
    userId: '',
    Gender: '',
    Age: '',
    Height: '',
    Weight: '',
    Duration: '',
    Heart_Rate: '',
    Body_Temp: '',
  });

  const [userId, setUserId] = useState('');
  const [result, setResult] = useState(null);

  const [currentWeight, setCurrentWeight] = useState('');
  const [targetWeight, setTargetWeight] = useState('');
  const [displayedText, setDisplayedText] = useState('');
  const [res, setRes] = useState(null);

  const handleChange = (e) => {
    setUserData({
      ...userData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://192.168.50.179:5000/save', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(userData),
    })
      .then((response) => response.json())
      .then((data) => {
        alert(data.message);
        setUserData({
          userId: '',
          Gender: '',
          Age: '',
          Height: '',
          Weight: '',
          Duration: '',
          Heart_Rate: '',
          Body_Temp: '',
        });
      })
      .catch((error) => {
        console.error('There was an error saving the data!', error);
        alert('An error occurred while saving the data.');
      });
  };

  const handleUserIdChange = (e) => {
    setUserId(e.target.value);
  };

  const calculateResult = () => {
    axios
      .get(`http://192.168.50.179:5000/res?userId=${userId}`)
      .then((response) => {
        if (response.data.result) {
          setResult(response.data.result['0'] + ' calories');
        } else {
          setResult('No data found for this User ID');
        }
      })
      .catch((error) => {
        console.error('Error fetching data: ', error);
        setResult('No data found for the provided UserId');
      });
  };

  const handleDietQuery = async () => {
    if (!currentWeight || !targetWeight) {
      alert('Please enter both weights');
      return;
    }

    const cw = parseFloat(currentWeight);
    const tw = parseFloat(targetWeight);

    if (isNaN(cw) || isNaN(tw)) {
      alert('Weights must be valid numbers');
      return;
    }

    const query =
      cw > tw
        ? 'What is the best diet plan for weight loss?'
        : 'What is the best diet plan for weight gain?';

    try {
      const response = await fetch('http://192.168.50.179:5000/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query }),
      });

      const data = await response.json();
      const words = data.result.split(' ');

      setRes(data.result);
      setDisplayedText('');

      let index = 0;
      const interval = setInterval(() => {
        if (index < words.length) {
          setDisplayedText((prev) => prev + words[index] + ' ');
          index++;
        } else {
          clearInterval(interval);
        }
      }, 150);
    } catch (error) {
      console.error('Error fetching diet plan:', error);
      setDisplayedText('Failed to fetch result.');
    }
  };

  return (
    <body>
    <div>
      <nav className="navbar" style={{ padding: '10px', background: '#f1f1f1' }}>
        <span style={{ fontWeight: 'bold', fontSize: '20px' }}>Health App</span>
        <div>
          <button
            onClick={() => window.location.href = '/'}
            style={{ color: 'black', marginRight: '50px', background: 'none', border: 'none', cursor: 'pointer' }}
          >
            Home
          </button>
          <button
            onClick={handleDietQuery}
            style={{ color: 'black', marginRight: '50px', background: 'none', border: 'none', cursor: 'pointer' }}
          >
            Diet plan
          </button>
        </div>
      </nav>

      <div style={{ padding: '20px' }}>
        <h1>Health Data Collection</h1>
        <form onSubmit={handleSubmit}>
          <input type="number" name="userId" placeholder="User ID" value={userData.userId} onChange={handleChange} required /><br />

          <select name="Gender" value={userData.Gender} onChange={handleChange}>
            <option value="">Gender</option>
            <option value="0">0 (Women)</option>
            <option value="1">1 (Men)</option>
          </select><br />

          <input type="number" name="Age" placeholder="Age" value={userData.Age} onChange={handleChange} required /><br />
          <input type="number" name="Height" placeholder="Height (cm)" value={userData.Height} onChange={handleChange} required /><br />
          <input type="number" name="Weight" placeholder="Weight (kg)" value={userData.Weight} onChange={handleChange} required /><br />
          <input type="number" name="Duration" placeholder="Duration (min)" value={userData.Duration} onChange={handleChange} required /><br />
          <input type="number" name="Heart_Rate" placeholder="Heart Rate" value={userData.Heart_Rate} onChange={handleChange} required /><br />
          <input type="number" name="Body_Temp" placeholder="Body Temperature" value={userData.Body_Temp} onChange={handleChange} required /><br />

          <button type="submit">Save Data</button>
        </form>

        <div style={{ marginTop: '20px' }}>
          <h2>Calculate Calories</h2>
          <input
            type="number"
            placeholder="Enter User ID"
            value={userId}
            onChange={handleUserIdChange}
            required
          />
          <button onClick={calculateResult}>Calculate</button>
        </div>

        {result && (
          <div style={{
            border: '1px solid #ccc',
            padding: '10px',
            marginTop: '10px',
            borderRadius: '5px',
            backgroundColor: '#f9f9f9',
            width: '200px',
            textAlign: 'center',
          }}>
            <span>{result}</span>
          </div>
        )}

        <div style={{ marginTop: '40px', fontFamily: 'Arial', maxWidth: '600px' }}>
          <h2>Diet Plan Generator</h2>

          <div style={{ marginBottom: '15px' }}>
            <label>Current Weight (kg): </label>
            <input
              type="number"
              value={currentWeight}
              onChange={(e) => setCurrentWeight(e.target.value)}
              style={{ padding: '8px', marginLeft: '10px' }}
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label>Target Weight (kg): </label>
            <input
              type="number"
              value={targetWeight}
              onChange={(e) => setTargetWeight(e.target.value)}
              style={{ padding: '8px', marginLeft: '23px' }}
            />
          </div>

          <button onClick={handleDietQuery} style={{ padding: '10px 20px' }}>
            Get Diet Plan
          </button>

         
        </div>
      </div>
    </div>
     {displayedText && (
            <div
              style={{
                marginTop: '30px',
                padding: '15px',
                backgroundColor: '#f9f9f9',
                borderRadius: '8px',
                fontSize: '17px',
                minHeight: '60px',
                // lineHeight: '1.6',
                whiteSpace: 'pre-wrap',
              }}
            >
              {displayedText}
            </div>
          )}
  </body>
  );
}

export default MainApp;
